INSERT INTO `department` VALUES ('7', '0', '内科', '医学科属。包括呼吸内科，消化内科，心血管内科，神经内科，肿瘤科，内分泌科，血液内科，传染病科，小儿科等等等。 ', '1', '1531188624');
INSERT INTO `department` VALUES ('8', '0', '外科', '外科是研究外科疾病的发生，发展规律及其临床表现，诊断，预防和治疗的科学，是以手术切除、修补为主要治病手段的专业科室。', '1', '1531188692');
INSERT INTO `department` VALUES ('9', '0', '妇产科', '妇产科是临床医学四大主要学科之一，主要研究女性生殖器官疾病的病因、病理、诊断及防治，妊娠、分娩的生理和病理变化等', '1', '1531188773');
INSERT INTO `department` VALUES ('10', '0', '男科', '许多专门针对男性病的科室。针对现代男性生活压力大，生殖泌尿系统疾病就诊数量猛增，而出现的专门服务男性的特殊科室。', '1', '1531188859');
INSERT INTO `department` VALUES ('11', '0', '儿科', '凡涉及儿童和青少年时期的健康与卫生问题都属于儿科范围。其医治对象处于生长发育期。', '1', '1531188930');
INSERT INTO `department` VALUES ('12', '0', '五官科', '所谓的“五官”，指的就是“耳、眉、眼、鼻、口”这五种人体器官。', '1', '1531188969');
INSERT INTO `department` VALUES ('13', '0', '肿瘤科', '肿瘤科和内科、外科、妇产科和儿科一样，是临床医学的二级学科，分为肿瘤内科、肿瘤放射治疗科和肿瘤外科等。', '1', '1531189020');
INSERT INTO `department` VALUES ('14', '0', '皮肤性病科', '皮肤科属于外科，主要治疗各种皮肤病，常见皮肤病有牛皮癣 、 疱疹 、酒渣鼻 、脓疱疮 、化脓菌感染 、疤痕 、癣等。', '1', '1531189177');
INSERT INTO `department` VALUES ('15', '0', '精神科', '精神科涉及到的疾病主要以精神病为主，是一组以表现在行为、心理活动上的紊乱为主的神经系统疾病。', '1', '1531189249');
INSERT INTO `department` VALUES ('16', '0', '营养科', '主要负责医院病人的肠内营养、肠外营养的会诊及营养液体的配制，鼻胃管、鼻肠管及中心静脉导管等营养通道的建立等工作。', '1', '1531189644');
INSERT INTO `department` VALUES ('17', '0', '整形外科', '整形外科学是外科学的一个分支，又称整复外科或成形外科，治疗范围主要是皮肤、肌肉及骨骼等创伤、疾病。', '1', '1531189840');
INSERT INTO `department` VALUES ('18', '0', '理疗科', '理疗科主要是利用人工或自然界物理因素作用于人体，使之产生有利的反应，达到预防和治疗疾病目的。', '1', '1531189876');
INSERT INTO `department` VALUES ('19', '0', '康复科', '康复医学科治疗的对象是残疾者以及各种功能障碍的慢性患者和老年患者，使他们伤病后能达到“病而不残，伤而不残，残而不废”。', '1', '1531190044');
INSERT INTO `department` VALUES ('20', '0', '职业病科', '。。。', '1', '1531190180');
INSERT INTO `department` VALUES ('21', '0', '输液科', '输液室是门诊病人静脉治疗的一个独立科室，是医院的窗口部门之一。', '1', '1531190387');
INSERT INTO `department` VALUES ('22', '0', '老年病科', '治疗老年疾病的科室。', '1', '1531190612');
INSERT INTO `department` VALUES ('23', '0', '中医科', '中医科采用中药治疗各种冠心病、心律失常、脑梗塞、脑动脉硬化、顽固性头痛、急慢性肾炎、泌尿系结石、男科病、脾胃病、糖尿病等', '1', '1531190687');
INSERT INTO `department` VALUES ('24', '0', '肝病科', '肝病科，是一个与许多科室交叉的学科，如感染科、消化内科、肝胆外科、普外科，其诊疗内容内、外兼顾，涉及范围较广。', '1', '1531190756');
INSERT INTO `department` VALUES ('25', '0', '感染科', '治疗各种感染性疾病的科室。', '1', '1531190787');
INSERT INTO `department` VALUES ('26', '0', '中西医结合科', '中西结合治疗疾病', '1', '1531190896');
INSERT INTO `department` VALUES ('27', '0', '疼痛科', '疼痛已被现代医学列为继呼吸、脉搏、血压、体温之后的第五大生命体征。', '1', '1531190979');
INSERT INTO `department` VALUES ('28', '0', '核医学科', '核医学科，利用核科学技术和手段对疾病进行诊断和治疗，是现代医学的主要手段之一。', '1', '1531191090');
