-- ----------------------------
-- Table structure for body_disease
-- ----------------------------
DROP TABLE IF EXISTS `body_disease`;
CREATE TABLE `body_disease` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `name` varchar(400) NOT NULL COMMENT '身体名称',
  `status_delete` int(10) unsigned NOT NULL COMMENT '逻辑删除',
  `create_time` int(10) unsigned NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=54 DEFAULT CHARSET=utf8 COMMENT='身体部位';

-- ----------------------------
-- Table structure for department
-- ----------------------------
DROP TABLE IF EXISTS `department`;
CREATE TABLE `department` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `pid` int(10) unsigned NOT NULL COMMENT '父id',
  `name` varchar(400) NOT NULL COMMENT '科室名称',
  `content` text COMMENT '科室描述',
  `status_delete` int(10) unsigned DEFAULT NULL COMMENT '逻辑删除',
  `create_time` int(10) unsigned NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=97 DEFAULT CHARSET=utf8 COMMENT='医院科室表';

-- ----------------------------
-- Table structure for disease
-- ----------------------------
DROP TABLE IF EXISTS `disease`;
CREATE TABLE `disease` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT 'id',
  `name` varchar(400) NOT NULL COMMENT '名称',
  `first_letter` varchar(10) DEFAULT NULL COMMENT '首字母',
  `department` int(10) unsigned DEFAULT NULL COMMENT '科室',
  `body_position` int(10) unsigned DEFAULT NULL COMMENT '身体部位',
  `summarize` text COMMENT '简介',
  `pathogenesis` text COMMENT '发病机理，病因',
  `symptoms` text COMMENT '症状',
  `diagnosis` text COMMENT '诊断',
  `treatment` text COMMENT '治疗',
  `prevention` text COMMENT '预防',
  `life` text COMMENT '生活',
  `drug` text COMMENT '药品',
  `status_delete` int(10) unsigned NOT NULL COMMENT '逻辑删除',
  `create_time` int(10) unsigned NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`),
  KEY `body_position` (`body_position`),
  KEY `department` (`department`),
  KEY `first_letter` (`first_letter`)
) ENGINE=InnoDB AUTO_INCREMENT=2554 DEFAULT CHARSET=utf8 COMMENT='疾病表';

-- ----------------------------
-- Table structure for doctor
-- ----------------------------
DROP TABLE IF EXISTS `doctor`;
CREATE TABLE `doctor` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `user_id` int(10) DEFAULT NULL,
  `realname` varchar(50) DEFAULT NULL COMMENT '姓名',
  `sex` tinyint(1) DEFAULT NULL COMMENT '性别',
  `hospital` varchar(255) DEFAULT NULL COMMENT '所在医院',
  `grade` varchar(50) DEFAULT NULL COMMENT '医生职称',
  `department_id` varchar(50) DEFAULT NULL COMMENT '科室',
  `introduce` text COMMENT '医生简介',
  `work_time_start` varchar(255) DEFAULT NULL COMMENT '开始上班时间',
  `work_time_end` varchar(255) DEFAULT NULL COMMENT '结束上班时间',
  `evaluate` int(10) DEFAULT NULL COMMENT '医生评级',
  `invite_code` varchar(10) DEFAULT NULL COMMENT '邀请码',
  `status_delete` tinyint(1) NOT NULL DEFAULT '1' COMMENT '逻辑删除',
  `create_time` int(10) DEFAULT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for drugs
-- ----------------------------
DROP TABLE IF EXISTS `drugs`;
CREATE TABLE `drugs` (
  `id` int(10) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL COMMENT '名称',
  `manufactor` text COMMENT '厂家',
  `firstprice` int(10) NOT NULL COMMENT '最大价格',
  `leastprice` int(10) NOT NULL COMMENT '最小价格',
  `drugsimg` text COMMENT '图片',
  `number` varchar(255) NOT NULL COMMENT '文字号',
  `treatment` text COMMENT '治疗疾病',
  `effect` text COMMENT '功能',
  `instructions` text COMMENT '说明书',
  `status_delete` int(10) NOT NULL COMMENT '逻辑删除',
  `create_time` int(10) NOT NULL COMMENT '创建时间',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=3 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Table structure for employee_system
-- ----------------------------
DROP TABLE IF EXISTS `employee_system`;
CREATE TABLE `employee_system` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `account` varchar(20) NOT NULL,
  `password` varchar(40) NOT NULL,
  `name` varchar(20) NOT NULL COMMENT '姓名',
  `sort` int(10) unsigned NOT NULL DEFAULT '0',
  `status` int(10) unsigned NOT NULL DEFAULT '1',
