  `patientid` int(10) NOT NULL COMMENT '病人id',
  `orderid` int(10) NOT NULL COMMENT '订单id',
  `now` text NOT NULL COMMENT '现病史',
  `history` text COMMENT '既往史',
  `personal` text COMMENT '个人史',
  `family` text COMMENT '家族史',
  `marriage` varchar(255) DEFAULT NULL COMMENT '婚姻史',
  `birth` varchar(255) DEFAULT NULL COMMENT '生育史',
  `testing` text COMMENT '检测(体温 血压等)',
